mapper.insert:
insert into chatbot(
	chatbot_no, chatbot_que, chatbot_answer
)
values(
	#{chatbotNo}, #{chatbotQue}, #{chatbotAnswer}
)

mapper.update:
update chatbot set
	chatbot_no = #{chatbotNo},
	chatbot_que = #{chatbotQue},
	chatbot_answer = #{chatbotAnswer}
where chatbot_no = #{chatbotNo}

update chatbot
chatbot_no = #{chatbotNo}, chatbot_que = #{chatbotQue}, chatbot_answer = #{chatbotAnswer},
where chatbot_no = #{chatbotNo}
